/**
 * Return type for Byte
 *
 * @category Utility
 */
export interface ByteType {
  type: "Wrapped";
  wrapper: "Byte";
  value: string;
}

/**
 * Pack many variables of the same type into a single array
 *
 * @example
 * ```tsx
 * Byte("");
 * ```
 *
 * @category Utility
 * @see https://docs.godotengine.org/en/stable/classes/class_packedbytearray.html
 */
export function Byte(value: string): ByteType {
  return {
    type: "Wrapped",
    wrapper: "Byte",
    value: value,
  };
}
